import React from 'react';

const InvoiceItem = ({ id, name, qty, price, onDeleteItem, onEdtiItem }) => {
  return (
    <tr className="border-b border-gray-900/10">
      {/* Item name input */}
      <td>
        <input
          type="text"
          name="name"
          id={id}
          placeholder="Item name"
          value={name}
          onChange={onEdtiItem}
          className="w-full rounded-md bg-gray-100 px-3 py-2 shadow-sm"
        />
      </td>

      {/* Quantity input */}
      <td>
        <input
          type="number"
          name="qty"
          id={id}
          min="1"
          step="1"
          value={qty}
          onChange={onEdtiItem}
          className="w-16 rounded-md bg-gray-100 px-2 py-2 shadow-sm"
        />
      </td>

      {/* Price input WITHOUT dollar sign */}
      <td className="text-center">
        <input
          type="number"
          name="price"
          id={id}
          min="0"
          step="0.01"
          value={price}
          onChange={onEdtiItem}
          className="w-24 rounded-md bg-gray-100 px-2 py-2 text-right shadow-sm"
        />
      </td>

      {/* Delete button */}
      <td className="text-center">
        <button
          type="button"
          onClick={() => onDeleteItem(id)}
          className="rounded bg-red-500 px-2 py-2 text-white hover:bg-red-600"
        >
          üóëÔ∏è
        </button>
      </td>
    </tr>
  );
};

export default InvoiceItem;
